mapper.select:
/* 특정 연도 or 지역의 예산 목록 조회 */ SELECT b.APPLY_YEAR, r.REGION_NAME, b.OFFICER_ID, b.BUDGET_REGION_AMOUNT, b.MUNHWA_REGION_NOP, b.REGIST_DT, b.UPDATE_DT FROM BUDGET_REGION AS b INNER JOIN REGION AS r ON b.REGION_CODE = r.REGION_CODE
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY b.APPLY_YEAR, b.REGION_CODE;

mapper.update:
/* 전국 단위 예산 수정 */ UPDATE BUDGET_TOTAL
OFFICER_ID = #{officerId}, BUDGET_BY_PERSON = #{budgetByPerson}, BUDGET_TOTAL_AMOUNT = #{budgetTotalAmount}, UPDATE_DT = CURDATE()
WHERE APPLY_YEAR = #{applyYear};

mapper.delete:
/* 전국 단위 예산 삭제 */
DELETE
FROM
    BUDGET_TOTAL
WHERE
    APPLY_YEAR=#{applyYear};
